mapper.select:
SELECT

FROM ${tableName} a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.cust_name LIKE
		'%'||#{custName}||'%'
		'%'+#{custName}+'%'
		concat('%',#{custName},'%')


		AND a.fapiaoshiqi BETWEEN #{beginFapiaoshiqi} AND #{endFapiaoshiqi}




		ORDER BY ${page.orderBy}


		ORDER BY  a.fapiaoshiqi DESC,a.danjuriqi DESC
